css:
.print-format div{
    text-align:center;
}
*{
    font-size:17px;
    line-height:1.5;
}
.header td:nth-child(1) {
    width: 45%;
}

table, th, td{
    font-size:20px;
}
.data td{
    border-bottom:1px solid black !important;
    border-top:1px solid black !important;
    border-right:1px solid black !important;
    border-left:1px solid black !important;

}
.data th{
    border-bottom:1px solid black !important;
    border-top:1px solid black !important;
    border-right:1px solid black !important;
    border-left:1px solid black !important;

}
