css:
.print-format div{
    border: 1px solid black;
    margin-top:10px;
    text-align:center;
}
.header tr{
    border-bottom: 1px solid black;
    font-size:10px;
    line-height: 1;
    font-weight:900;
}
.header th{
    font-size:12px;
    text-align:center;
}
.header tr:last-child {
  border: 0;
}
.header td{
    text-align:left;
    border-right: 1px solid black;
}
.header td:last-child {
  border: 0;
}
